function addFun(c){var b=window.onload;if(typeof window.onload!="function"){window.onload=c}else{window.onload=function(){b();c()}}}function addNode(d,c){var e=c.parentNode;if(e.lastChild==c){e.appendChild(d)}else{e.insertBefore(d,c.nextSibling)}}function showPic(e){var d=e.getAttribute("href");var c=document.getElementById("placeholder");c.setAttribute("src",d);var b=e.getAttribute("title");var f=document.getElementById("p1");f.firstChild.nodeValue=b;return false}function addOn(){var c=document.getElementById("gallery");var b=c.getElementsByTagName("a");for(var d=0;d<b.length;d++){b[d].onclick=function(){return showPic(this)}}}function create(){var c=document.createElement("img");c.setAttribute("id","placeholder");c.setAttribute("src","../../images/placeholder.png");c.setAttribute("alt","beijing");var a=document.createElement("p");a.setAttribute("id","p1");var d=document.createTextNode("beijing");a.appendChild(d);var b=document.getElementById("gallery");addNode(c,b);addNode(a,b)}addFun(create);addFun(addOn);